#include <iostream>#include <string>#include <string.h>using namespace std;void findvowel(string str){	int nv=0;	int i;	for (i = 0; i < str.length(); ++i)	{		switch(toupper(str[i])){			case 'A':			case 'E':			case 'I':			case 'O':			case 'U': nv++;			break;		}			}	if(i==nv){		cout<<"yes"<<endl;	}	else{		cout<<"No"<<endl;	}}int main(int argc, char const *argv[]){	string str;	cout<<"Enter String"<<endl;	cin>>str;	findvowel(str);	return 0;}